import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';
import { Observable } from 'rxjs';
import { IErrorsHttpModel } from '../../_models/iErrorsHttp-model';

@Component({
  selector: 'app-error-dialog',
  templateUrl: './error-dialog.component.html',
  styleUrls: ['./error-dialog.component.scss']
})
export class ErrorDialogComponent {

  /**DialogData é o tipo pode ser o Observable de error do service como um string, usaremos o Observable error
  ******constructor(@Inject(MAT_DIALOG_DATA) public data: string) {} *****/

  constructor(@Inject(MAT_DIALOG_DATA) public data: IErrorsHttpModel) { }

}
<div class="dataMaterial">
  <!-- <p>{{ this.data.name +': ' }}  {{ this.data.statusText }} {{ this.data.status }}</p>
  <p>{{ this.data.url }}</p> -->
  <h1 mat-dialog-title style="color: rgba(255, 0, 0, 0.705);">{{ this.data.name +': ' }} {{ this.data.statusText + ': '}} {{ this.data.status }}</h1>
  <div mat-dialog-content>{{ this.data.url }}</div>
  <div mat-dialog-actions class="localButton">
    <button mat-stroked-button color="accent" mat-dialog-close>Close</button>
  </div>
</div>
